package lwc131;

public class ShapeTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Rectangle r = new Rectangle();
		r.move(10,10);
		System.out.println("사각형 그리기 메쏘드");
		Circle c = new Circle();
		c.move(10,10);
		System.out.println("원 그리기 메쏘드");
		Triangle t = new Triangle();
		t.move(10,10);
		System.out.println("삼각형 그리기 메쏘드");

	}

}
